/*Table structure for table `worker` */

DROP TABLE IF EXISTS `worker`;

CREATE TABLE `worker` (
                          `num` int(255) NOT NULL AUTO_INCREMENT COMMENT '序号',
                          `userid` varchar(16) NOT NULL COMMENT '工号',
                          `password` varchar(16) NOT NULL COMMENT '密码',
                          `headpath` varchar(90) DEFAULT NULL COMMENT '头像路径',
                          `name` varchar(16) DEFAULT NULL COMMENT '姓名',
                          `phone` varchar(11) DEFAULT NULL COMMENT '手机号',
                          `gender` char(1) DEFAULT NULL COMMENT '性别',
                          `mail` varchar(30) DEFAULT NULL COMMENT '邮箱',
                          `type_worker` tinyint(9) DEFAULT NULL COMMENT '工种',
                          `position` varchar(30) DEFAULT NULL COMMENT '职位',
                          `workstate` char(1) DEFAULT 'Y' COMMENT '当班情况（Y/N)-上班/请假',
                          `sum_eval` float DEFAULT '0' COMMENT '总的评分',
                          `createdate` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '注册时间',
                          PRIMARY KEY (`num`,`userid`),
                          KEY `type_worker` (`type_worker`),
                          CONSTRAINT `worker_ibfk_1` FOREIGN KEY (`type_worker`) REFERENCES `type_worker` (`num`)
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

